insertion-points.methods:

public function generateMessageRef(?string $tag = null): MessageRef
{
    return new MessageRef(static::schema()->getCurie(), $this->fget(self::NAME_FIELD), $tag);
}

public function getUriTemplateVars(): array
{
    return [
        'name' => $this->fget(self::NAME_FIELD),
    ];
}


/**
 * @param {?string} tag
 * @returns {MessageRef}
 */
generateMessageRef(tag = null) {
  return new MessageRef(this.schema().getCurie(), this.get(this.NAME_FIELD), tag);
}

/**
 * @returns {Object}
 */
getUriTemplateVars() {
  return {
    name: this.get(this.NAME_FIELD),
  };
}
